import{_ as t,c as e,o,ag as i}from"./chunks/framework.-4pDR0ji.js";const p=JSON.parse('{"title":"Getting Started","description":"","frontmatter":{},"headers":[],"relativePath":"guide/getting-started.md","filePath":"guide/getting-started.md"}'),r={name:"guide/getting-started.md"};function n(l,a,s,c,d,h){return o(),e("div",null,[...a[0]||(a[0]=[i('<h1 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to &quot;Getting Started&quot;">​</a></h1><p>Welcome to Tafil! This guide will help you get up and running quickly.</p><h2 id="what-is-tafil" tabindex="-1">What is Tafil? <a class="header-anchor" href="#what-is-tafil" aria-label="Permalink to &quot;What is Tafil?&quot;">​</a></h2><p>Tafil is a desktop application for managing Node.js projects. It helps you:</p><ul><li><strong>Scan</strong> directories to find all your Node.js projects</li><li><strong>Organize</strong> projects into collections</li><li><strong>Launch</strong> dev servers with one click</li><li><strong>Open</strong> projects in your favorite IDE</li></ul><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><p>Download Tafil for your platform:</p><ul><li><a href="/Tafil/docs/downloads/macos.html">macOS</a></li><li><a href="/Tafil/docs/downloads/windows.html">Windows</a></li><li><a href="/Tafil/docs/downloads/linux.html">Linux</a></li></ul><h2 id="quick-start" tabindex="-1">Quick Start <a class="header-anchor" href="#quick-start" aria-label="Permalink to &quot;Quick Start&quot;">​</a></h2><h3 id="_1-launch-tafil" tabindex="-1">1. Launch Tafil <a class="header-anchor" href="#_1-launch-tafil" aria-label="Permalink to &quot;1. Launch Tafil&quot;">​</a></h3><p>Open Tafil from your Applications folder (macOS), Start Menu (Windows), or app launcher (Linux).</p><h3 id="_2-scan-your-projects" tabindex="-1">2. Scan Your Projects <a class="header-anchor" href="#_2-scan-your-projects" aria-label="Permalink to &quot;2. Scan Your Projects&quot;">​</a></h3><p>Click <strong>Scan Home</strong> to find all Node.js projects in your home directory.</p><p>Or use <strong>Scan Folder</strong> to scan a specific directory.</p><h3 id="_3-run-a-project" tabindex="-1">3. Run a Project <a class="header-anchor" href="#_3-run-a-project" aria-label="Permalink to &quot;3. Run a Project&quot;">​</a></h3><p>Click the <strong>Play</strong> button on any project card to start its dev server.</p><h3 id="_4-open-in-ide" tabindex="-1">4. Open in IDE <a class="header-anchor" href="#_4-open-in-ide" aria-label="Permalink to &quot;4. Open in IDE&quot;">​</a></h3><p>Click the <strong>Code</strong> icon to open the project in your default IDE.</p><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li><a href="/Tafil/docs/guide/scanning.html">Scanning Projects</a> - Learn about project detection</li><li><a href="/Tafil/docs/features/collections.html">Collections</a> - Organize your projects</li><li><a href="/Tafil/docs/features/command-palette.html">Command Palette</a> - Keyboard shortcuts</li></ul>',20)])])}const f=t(r,[["render",n]]);export{p as __pageData,f as default};
